// https://styled-components.com/
import styled, { css } from 'styled-components';

export default styled.a`
  background: transparent;
  border-radius: 5px;
  border: 2px solid palevioletred;
  color: palevioletred;
  margin: 5px 5px;
  padding: 0.25em 0em;
  width: 90px;
  height: 50px;

  ${props => props.primary && css`
    background: palevioletred;
    color: white;
  `}
`;
